mapper.select:
SELECT a.user_id AS userId, IF(a.my_nickname = '', b.nickname, a.my_nickname) AS nickname, b.avatar_file_id AS avatarFileId FROM base_im_group_ref_user a LEFT JOIN base_user_info b ON b.id = a.user_id WHERE a.group_id = #{dto.groupId} AND b.id IS NOT NULL
AND a.user_id = #{dto.userId}
AND (a.my_nickname LIKE CONCAT('%', #{dto.nickname}, '%') OR b.nickname LIKE CONCAT('%', #{dto.nickname}, '%'))

SELECT a.user_id AS userId, IF(a.my_nickname = '', b.nickname, a.my_nickname) AS nickname, b.avatar_file_id AS avatarFileId FROM base_im_group_ref_user a LEFT JOIN base_user_info b ON b.id = a.user_id WHERE a.group_id = #{dto.groupId} AND a.mute_flag = TRUE AND b.id IS NOT NULL
AND a.user_id = #{dto.userId}
AND (a.my_nickname LIKE CONCAT('%', #{dto.nickname}, '%') OR b.nickname LIKE CONCAT('%', #{dto.nickname}, '%'))
