mapper.select:
SELECT b.id AS contentId, b.create_ts AS createTs, b.type AS type, b.content AS content, b.create_id AS createId, b.ref_id AS refId
FROM base_im_session_content_ref_user a LEFT JOIN base_im_session_content b ON b.id = a.content_id
WHERE a.user_id = #{currentUserId}
AND a.show_flag = TRUE
AND b.enable_flag = TRUE
AND a.session_id = #{dto.sessionId}
AND b.content LIKE CONCAT('%', #{dto.content}, '%')
AND a.content_id = #{dto.contentId}
AND a.content_id > #{dto.contentId}
AND a.content_id < #{dto.contentId}
ORDER BY b.create_ts DESC, b.order_no DESC
